import React, {useState} from "react";

function App(){
  const [count, setState] = useState(0);

  function increase(){
    setState(count+1);
  }
  function decrease(){
    setState(count-1);
  }
return (
  <div>
    <div className="container">
      <h1>TIME</h1>
      <button>Get Time</button>
    </div>
  </div>
);
}
export default App;
