import {createStore} from 'redux'


const createReducer = (state = {counter :0}, action)=> {
    if(action.type === 'increment'){
        return state.counter + 1
    }
    if(action.type === 'decrement'){
        return state.counter - 1
    }
    return state;
}
const store = createStore(createReducer);

export default store;
